b = sqrt(3)/2*5;
c = 5/2;
s = 8;

A = (-s/2,-sqrt(3)/2*(s - 5));
B = (0,b);
C = (s/2,-sqrt(3)/2*(s - 5));
F[1] = (c,0);
F[2] = (-c,0);
M = (A + C)/2;

